<?phpnamespace App\Http\Middleware;use App\Member;use Closure;use Illuminate\Support\Facades\Auth;class AdminAuthenticate{    /**     * Handle an incoming request.     *     * @param  \Illuminate\Http\Request $request     * @param  \Closure $next     * @return mixed     */    public function handle($request, Closure $next)    {        if (!Auth::check()) {            return redirect('login');        }        return $next($request);    }}